import React, { useReducer, useEffect, useRef, useState } from 'react';
import HomePage from './HomePage';
import { verifyLogin } from './utils';

const initialState = {
  username: '',
  password: '',
  isLoading: false,
  isLoggedIn: false,
  error: ''
};

function loginReducer(state, action) {
  switch (action.type) {
    case 'field': {
      return {
        ...state,
        [action.fieldName]: action.payload
      };
    }
    case 'login': {
      return {
        ...state,
        error: '',
        isLoading: true,
        isFocused: true
      };
    }
    case 'success': {
      return {
        ...state,
        isLoggedIn: true,
        isLoading: false
      };
    }
    case 'error': {
      return {
        ...state,
        error: 'Incorrect username or password entered',
        isLoggedIn: false,
        isLoading: false,
        username: '',
        password: '',
        isFocused: true
      };
    }
    case 'logout': {
      return {
        ...state,
        isLoggedIn: false,
        username: '',
        password: '',
        error: ''
      };
    }
    default:
      return state;
  }
}

export default function LoginWithReducer() {
  const [state, dispatch] = useReducer(loginReducer, initialState);
  const { username, password, isLoading, isLoggedIn, error, isFocused } = state;
  const [loggedIn, setLoggedIn] = useState(false);
  const usernameRef = useRef(null);

  const handleSubmit = async e => {
    e.preventDefault();
    dispatch({ type: verifyLogin });
    try {
      await verifyLogin({ username, password });
      dispatch({ type: 'success' });
      localStorage.setItem("logged_in", true);
      setLoggedIn(true)
    } catch (error) {
      dispatch({ type: 'error' });
      localStorage.setItem("logged_in", false);
    }
  };

  useEffect(() => {
    if (isFocused) {
      usernameRef.current.focus();
    }
    console.log(localStorage.getItem("logged_in"))
    if (localStorage.getItem("logged_in")){
      setLoggedIn(true)
    } else {
      setLoggedIn(false)
    }
  }, [isFocused]);

  const handleLogout = () => {
    localStorage.setItem("logged_in", false);
    dispatch({ type: 'logout' });
    setLoggedIn(false)
    console.log('logged out')
  }

  return (
    <div className="App">
     
        {loggedIn ? (
          <>
            {/* <h1>Welcome {username}!</h1> */}
            <HomePage username={username} logout={handleLogout}/>
            {/* <button onClick={() => dispatch({ type: 'logout' })}>
              Log Out
            </button> */}
          </>
        ) : (
          <div className="login-container">
          <form className="form" onSubmit={handleSubmit}>
            {error && <p className="error">{error} </p>}
            <p>Please Login!</p>
            <input
              type="text"
              ref={usernameRef}
              placeholder="Enter username"
              value={username}
              autoFocus
              onChange={e =>
                dispatch({
                  type: 'field',
                  fieldName: 'username',
                  payload: e.currentTarget.value
                })
              }
            />
            <input
              type="password"
              placeholder="Enter password"
              value={password}
              onChange={e =>
                dispatch({
                  type: 'field',
                  fieldName: 'password',
                  payload: e.currentTarget.value
                })
              }
            />
            <button className="submit" type="submit" disabled={isLoading}>
              {isLoading ? 'Logging In.....' : 'Log In'}
            </button>
          </form>
          </div>
        )}

    </div>
  );
}
